mapper.select:

left join t_metadata m on m.id = t.metadata_id


    t.metadata_id = #{parameters.metadataId}


    and m.code like concat(#{parameters.metadataCode}, '%')

mapper.insert:
insert into t_data
values (#{id}, #{metadata.id});

  insert into t_data_element values (uuid(), #{element.code}, #{element.content}, #{id},#{element.property.id});

mapper.delete:
delete
from t_data
where id = #{id};
delete
from t_data_element
where data_id = #{id};
