select.where:
t.is_delete = ${@cn.koala.enhancement.YesNo@NO.value}

  and t.name like concat(#{parameters.name}, '%')


  and t.dictionary_id = #{parameters.dictionaryId}

mapper.update:
update t_dictionary_item
name=#{name}, content=#{content},
where is_delete = ${@cn.koala.enhancement.YesNo@NO.value} and id = #{id}

update t_dictionary_item
set is_delete = ${@cn.koala.enhancement.YesNo@YES.value}
where id = #{id}

update t_dictionary_item
set is_delete = ${@cn.koala.enhancement.YesNo@YES.value}
where dictionary_id = #{dictionaryId}
