mapper.update:
update k_permission
code=#{code}, name=#{name}, type=#{type}, icon=#{icon}, url=#{url}, component=#{component}, remark=#{remark}, parent_id=#{parentId}, last_modified_by = #{lastModifiedBy}, last_modified_time = #{lastModifiedTime},
where is_deleted = ${@cn.koala.persist.domain.YesNo@NO.value} and id=#{id}

update k_permission
set is_deleted   = ${@cn.koala.persist.domain.YesNo@YES.value},
    deleted_by   = #{deletedBy},
    deleted_time = #{deletedTime}
where id = #{id}
