select.where:
t.is_deleted = ${@cn.koala.persist.domain.YesNo@NO.value}

  and t.code like concat(#{code}, '%')


  and t.name like concat(#{name}, '%')

mapper.update:
update k_setting
code=#{code}, name=#{name}, content=#{content}, remark=#{remark}, last_modified_by = #{lastModifiedBy}, last_modified_time = #{lastModifiedTime},
where is_deleted = ${@cn.koala.persist.domain.YesNo@NO.value} and id=#{id}

update k_setting
set is_deleted   = ${@cn.koala.persist.domain.YesNo@YES.value},
    deleted_by   = #{deletedBy},
    deleted_time = #{deletedTime}
where id = #{id}
