select.where:
t.is_delete = 0

  and t.name like concat(#{parameters.name}, '%')


  and t.group_id = #{parameters.groupId}

mapper.update:
update t_template
name=#{name}, content=#{content}, group_id=#{group.id},
where is_delete = 0 and id = #{id}

update t_template
set is_delete = 1
where id = #{id}
